import React, { useState } from "react";
import "./ResponsablePage.css";

const ResponsablePage = () => {
  const [activeTab, setActiveTab] = useState("dashboard");

  return (
    <div className="responsable-page">
      <h1>Gestionnaire de Présence</h1>

      <div className="tabs">
        <button
          className={`tab ${activeTab === "dashboard" ? "active" : ""}`}
          onClick={() => setActiveTab("dashboard")}
        >
          Tableau de bord
        </button>
        <button
          className={`tab ${activeTab === "attendance" ? "active" : ""}`}
          onClick={() => setActiveTab("attendance")}
        >
          Présences
        </button>
        <button
          className={`tab ${activeTab === "guests" ? "active" : ""}`}
          onClick={() => setActiveTab("guests")}
        >
          Invités
        </button>
      </div>

      <div className="tab-content">
        {activeTab === "dashboard" && (
          <div className="dashboard-grid">
            <div className="stat-card">
              <h3>Todays Courses</h3>
              <p className="stat">25</p>
            </div>
            <div className="stat-card">
              <h3>Todays Presences</h3>
              <p className="stat">8%</p>
            </div>
            <div className="stat-card">
              <h3>Total Students</h3>
              <p className="stat">140</p>
            </div>
          </div>
        )}

        {activeTab === "attendance" && (
          <table className="attendance-table">
            <thead>
              <tr>
                <th>#</th>
                <th>Nom</th>
                <th>Heure d'arrivée</th>
                <th>Heure de départ</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>1</td>
                <td>John Doe</td>
                <td>08:00</td>
                <td>17:00</td>
              </tr>
            </tbody>
          </table>
        )}

        {activeTab === "guests" && (
          <button className="add-guest-btn">Ajouter un invité</button>
        )}
      </div>
    </div>
  );
};

export default ResponsablePage;
