import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "./PresentList.css";

const socket = io("http://localhost:5000");

const PresentList = () => {
  const [persons, setPersons] = useState([]);
  const [todaysPresence, setTodaysPresence] = useState([]);

  useEffect(() => {
    const fetchPersons = async () => {
      try {
        const response = await fetch(
          "http://localhost:5000/api/get-all-persons"
        );
        const data = await response.json();
        console.log("Fetched persons:", data); // Log the data
        setPersons(data);
      } catch (error) {
        console.error("Error fetching persons:", error);
      }
    };

    const fetchTodaysPresence = async () => {
      try {
        const response = await fetch(
          "http://localhost:5000/api/get-todays-presence"
        );
        const data = await response.json();
        console.log("Fetched today's presence:", data); // Log the data
        setTodaysPresence(data);
      } catch (error) {
        console.error("Error fetching today's presence:", error);
      }
    };

    fetchPersons();
    fetchTodaysPresence();

    socket.on("presence_updated", (data) => {
      console.log("Presence updated:", data);
      setTodaysPresence((prev) => [...prev, data]);
    });

    socket.on("person_added", (data) => {
      console.log("Person added:", data);
      setPersons((prev) => [...prev, { id: Date.now(), name: data.name }]);
    });

    return () => {
      socket.off("presence_updated");
      socket.off("person_added");
    };
  }, []);

  useEffect(() => {
    console.log("todaysPresence:", todaysPresence); // Log the todaysPresence array
  }, [todaysPresence]);

  const isPresentToday = (personName) => {
    return (
      Array.isArray(todaysPresence) &&
      todaysPresence.some((p) => p.name === personName)
    );
  };

  const getPresenceDate = (personName) => {
    if (!Array.isArray(todaysPresence)) return "Absent";
    const presence = todaysPresence.find((p) => p.name === personName);
    return presence ? presence.date : "-";
  };

  return (
    <div className="List">
      <h2>Present List</h2>
      <div>
        <table cellspacing="50">
          <thead>
            <tr>
              <th>Name</th>
              <th>Presence Status</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {persons.map((person) => (
              <tr key={person.id}>
                <td>{person.name}</td>
                <td>{isPresentToday(person.name) ? "Present" : "Absent"}</td>
                <td>{getPresenceDate(person.name)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PresentList;
