import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "./PresentList.css";

const socket = io("http://localhost:5000", {
  transports: ["websocket", "polling"],
  withCredentials: true,
});

const PresentList = () => {
  const [persons, setPersons] = useState([]);

  useEffect(() => {
    const fetchPersons = async () => {
      try {
        const response = await fetch(
          "http://localhost:5000/api/get-all-persons"
        );
        const data = await response.json();
        console.log("Fetched persons:", data);
        setPersons(data);
      } catch (error) {
        console.error("Error fetching persons:", error);
      }
    };

    fetchPersons();

    socket.on("person_added", (newPerson) => {
      setPersons((prev) => [...prev, newPerson]);
    });

    socket.on("presence_updated", (updatedData) => {
      setPersons((prev) =>
        prev.map((person) =>
          person.id === updatedData.id ? updatedData : person
        )
      );
    });

    socket.on("data_update", (data) => {
      setPersons(data);
    });

    // Clean up
    return () => {
      socket.off("person_added");
      socket.off("presence_updated");
      socket.off("data_update");
    };
  }, []);

  return (
    <div className="List">
      <h2>Present List</h2>
      <div className="table">
        <div className="table-header">
          <div className="table-row">
            <div className="table-cell">Name</div>
            <div className="table-cell">Presence Status</div>
            <div className="table-cell">Date</div>
          </div>
        </div>
        <div className="table-body">
          {persons.map((person) => (
            <div className="table-row" key={person.id}>
              <div className="table-cell">{person.name}</div>
              <div className="table-cell">
                {person.presence_date ? "Present" : "Absent"}
              </div>
              <div className="table-cell">
                {person.presence_date ? person.presence_date : "-"}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PresentList;
