import React, { useEffect, useState, useMemo } from "react";
import io from "socket.io-client";
import "./PresentList.css";

const socket = io("http://localhost:5000", {
  transports: ["websocket", "polling"],
  withCredentials: true,
  reconnection: true,
  reconnectionDelay: 1000,
  reconnectionAttempts: 5,
});

const PresentList = () => {
  const [persons, setPersons] = useState([]);
  const [isConnected, setIsConnected] = useState(socket.connected);
  const [loading, setLoading] = useState(true);

  const fetchPersons = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/get-all-persons");
      const data = await response.json();
      console.log("Fetched persons:", data);
      setPersons(data);
    } catch (error) {
      console.error("Error fetching persons:", error);
    } finally {
      setLoading(false);
    }
  };

  const formattedDate = useMemo(() => {
    const today = new Date();
    const month = String(today.getMonth() + 1).padStart(2, "0");
    const date = String(today.getDate()).padStart(2, "0");
    const year = today.getFullYear();
    return `${month}/${date}/${year}`;
  }, []);

  useEffect(() => {
    // Connection event handlers
    socket.on("connect", () => {
      console.log("Socket connected");
      setIsConnected(true);
      fetchPersons(); // Re-fetch data when connection establishes
    });

    socket.on("disconnect", () => {
      console.log("Socket disconnected");
      setIsConnected(false);
    });

    // Initial data fetch
    fetchPersons();

    // Data update handlers
    socket.on("persons_data", (data) => {
      console.log("Received persons data update:", data);
      setPersons(data);
    });

    // Cleanup
    return () => {
      socket.off("connect");
      socket.off("disconnect");
      socket.off("persons_data");
    };
  }, []);

  if (loading) {
    return <div className="List">Loading...</div>;
  }
  console.log("date" + date);

  return (
    <div className="List">
      <h2>Present List {formattedDate}</h2>
      {!isConnected && (
        <div className="connection-warning">Connecting to server...</div>
      )}
      <div className="table">
        <div className="table-header">
          <div className="table-row">
            <div className="table-cell">Name</div>
            <div className="table-cell">Presence Status</div>
            <div className="table-cell">Date</div>
          </div>
        </div>
        <div className="table-body">
          {persons.map((person) => (
            <div className="table-row" key={person.id}>
              <div className="table-cell">{person.name}</div>
              <div className="table-cell">
                {person.presence_date ? "Present" : "Absent"}
              </div>
              <div className="table-cell">
                {person.presence_date ? person.presence_date : "-"}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PresentList;
