import React, { useState } from "react";
import "./ResponsablePage.css";
import Dashboard from "./dashboard/dashboard";

const ResponsablePage = () => {
  const [activeTab, setActiveTab] = useState("dashboard");

  return (
    <div className="responsable-page">
      <h1>Gestionnaire de Présence</h1>

      <div className="tabs">
        <button
          className={`tab ${activeTab === "dashboard" ? "active" : ""}`}
          onClick={() => setActiveTab("dashboard")}
        >
          Tableau de bord
        </button>
        <button
          className={`tab ${activeTab === "attendance" ? "active" : ""}`}
          onClick={() => setActiveTab("attendance")}
        >
          Présences
        </button>
        <button
          className={`tab ${activeTab === "guests" ? "active" : ""}`}
          onClick={() => setActiveTab("guests")}
        >
          Manage Courses
        </button>
      </div>

      <div className="tab-content">
        {activeTab === "dashboard" && <Dashboard />}

        {activeTab === "attendance" && (
          <table className="attendance-table">
            <thead>
              <tr>
                <th>#</th>
                <th>Nom</th>
                <th>Heure d'arrivée</th>
                <th>Heure de départ</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>1</td>
                <td>John Doe</td>
                <td>08:00</td>
                <td>17:00</td>
              </tr>
            </tbody>
          </table>
        )}

        {activeTab === "guests" && (
          <button className="add-guest-btn">Ajouter un invité</button>
        )}
      </div>
    </div>
  );
};

export default ResponsablePage;
