import React, { useRef, useEffect, useState } from "react";

const VideoCapture = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [backendResponse, setBackendResponse] = useState({});

  useEffect(() => {
    // Access the user's camera
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;

          // Ensure the video element is loaded and ready to play
          videoRef.current.onloadedmetadata = () => {
            videoRef.current.play();
          };
        }
      })
      .catch((err) => {
        console.error("Error accessing camera: ", err);
      });

    return () => {
      // Clean up the camera stream on unmount
      if (videoRef.current && videoRef.current.srcObject) {
        const tracks = videoRef.current.srcObject.getTracks();
        tracks.forEach((track) => track.stop());
      }
    };
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      captureFrameAndSend();
    }, 5000); // Capture every 5 seconds

    return () => clearInterval(interval);
  }, []);

  const captureFrameAndSend = async () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;

    if (!canvas || !video) return;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const context = canvas.getContext("2d");
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    const frameData = canvas.toDataURL("image/png");

    try {
      const response = await fetch("http://localhost:5000/process-frame", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ frame: frameData }),
      });

      const data = await response.json();
      setBackendResponse(JSON.stringify(data));
      console.log("Backend response:", JSON.stringify(data.success));
    } catch (error) {
      console.error("Error sending frame:", error);
    }
  };

  return (
    <div>
      <h1>Video Capture</h1>

      <video ref={videoRef} style={{ width: "400px" }} />
      <canvas ref={canvasRef} style={{ display: "none" }} />
      <p>
        Last Response:{" "}
        {backendResponse?.success
          ? backendResponse.success
          : "Waiting for response..."}
      </p>
    </div>
  );
};

export default VideoCapture;
