import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "./PresentList.css";

const socket = io("http://localhost:5000", {
  transports: ["websocket", "polling"],
  withCredentials: true,
});

const PresentList = () => {
  const [persons, setPersons] = useState([]);
  const [todaysPresence, setTodaysPresence] = useState([]);

  useEffect(() => {
    const fetchPersons = async () => {
      try {
        const response = await fetch(
          "http://localhost:5000/api/get-all-persons"
        );
        const data = await response.json();
        console.log("Fetched persons:", data);
        setPersons(data);
      } catch (error) {
        console.error("Error fetching persons:", error);
      }
    };

    const fetchTodaysPresence = async () => {
      try {
        const response = await fetch(
          "http://localhost:5000/api/get-todays-presence"
        );
        const data = await response.json();
        console.log("Fetched today's presence:", data); // Log the data
        setTodaysPresence(data);
      } catch (error) {
        console.error("Error fetching today's presence:", error);
      }
    };

    fetchPersons();
    fetchTodaysPresence();

    socket.on("presence_updated", (data) => {
      console.log("Presence updated:", data);
      setTodaysPresence((prev) => {
        const updatedPresence = [...prev, data];
        return updatedPresence;
      });
    });

    socket.on("person_added", (data) => {
      console.log("Person added:", data);
      setPersons((prev) => [...prev, { id: Date.now(), name: data.name }]);
    });

    return () => {
      socket.off("presence_updated");
      socket.off("person_added");
    };
  }, []);

  useEffect(() => {
    console.log("todaysPresence:", todaysPresence); // Log the todaysPresence array
  }, [todaysPresence]);

  const isPresentToday = (personName) => {
    return (
      Array.isArray(todaysPresence) &&
      todaysPresence.some((p) => p.name === personName)
    );
  };

  const getPresenceDate = (personName) => {
    if (!Array.isArray(todaysPresence)) return "Absent";
    const presence = todaysPresence.find((p) => p.name === personName);
    return presence ? presence.presence_date : "-";
  };

  return (
    <div className="List">
      <h2>Present List</h2>
      <div className="table">
        <div className="table-header">
          <div className="table-row">
            <div className="table-cell">Name</div>
            <div className="table-cell">Presence Status</div>
            <div className="table-cell">Date</div>
          </div>
        </div>
        <div className="table-body">
          {persons.map((person) => (
            <div className="table-row" key={person.id}>
              <div className="table-cell">{person.name}</div>
              <div className="table-cell">
                {isPresentToday(person.name) ? "Present" : "Absent"}
              </div>
              <div className="table-cell">{getPresenceDate(person.name)}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
export default PresentList;
