import React, { useState } from "react";
import "./Navbar.css";

const Navbar = () => {
  const [name, setName] = useState("");
  const [image, setImage] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const handleSubmit = async () => {
    if (name && image) {
      const formData = new FormData();
      formData.append("name", name);
      formData.append("image", image);

      try {
        const response = await fetch("/api/modify-database", {
          method: "POST",
          body: formData,
        });
        if (response.ok) {
          alert("Data submitted successfully");
        } else {
          alert("Failed to submit data");
        }
      } catch (error) {
        console.error("Error submitting data", error);
        alert("Failed to submit data");
      }
    } else {
      alert("Please fill in both fields");
    }
  };

  return (
    <div className="navbar">
      <div className="navbar-title">Today's Presence</div>
      <button onClick={() => setShowForm(!showForm)} className="navbar-button">
        {showForm ? "Hide Form" : "Show Form"}
      </button>
      {showForm && (
        <div className="navbar-card">
          <div className="navbar-form">
            <input
              type="text"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="navbar-input"
            />
            <input
              type="file"
              onChange={(e) => setImage(e.target.files[0])}
              className="navbar-input"
            />
            <button onClick={handleSubmit} className="navbar-button">
              Modify Database
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Navbar;
